    public Matrix getMatrix(char character) {
        return getMatrix(character+"");
    }

    public Matrix getMatrix(String character) {
        int[][] array = chars.get(character.substring(0, 1));
        if (array == null) {
            return new Matrix();
        }
        return Matrix.fromArray(array);
    }

    public Matrix arrowUp() {
        return getMatrix("^");
    }

